mapper.insert:
INSERT INTO USERS	/* userInsert */
(
	USR_ID
	, PASSWORD
	, USR_NM
	, PHONE1_1
	, PHONE1_2
	, PHONE1_3
	, EMAIL
	, WRTR
	, UPDTR
	, WRT_DT
	, UPD_DT
)
VALUES
(
	#{usrId}
	, #{password}
	, #{usrNm}
	, #{phone1_1}
	, #{phone1_2}
	, #{phone1_3}
	, #{email}
	, #{wrtr}
	, #{updtr}
	, CURRENT_TIMESTAMP
	, CURRENT_TIMESTAMP
)

mapper.update:
UPDATE
	USERS
SET
	PASSWORD = #{password}
	, USR_NM = #{usrNm}
	, PHONE1_1 = #{phone1_1} 
	, PHONE1_2 = #{phone1_2}
	, PHONE1_3 = #{phone1_3}
	, EMAIL = #{email}
	, UPDTR = #{updtr}
	, UPD_DT = CURRENT_TIMESTAMP
WHERE
	USR_ID = #{usrId}
